CREATE TABLE `tmp_content` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `elementId` int(11) NOT NULL,
  `locale` char(12) COLLATE utf8_unicode_ci NOT NULL,
  `title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `field_body` text COLLATE utf8_unicode_ci,
  `field_postSummary` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `field_pageDescription` varchar(140) COLLATE utf8_unicode_ci DEFAULT NULL,
  `field_pageTitle` text COLLATE utf8_unicode_ci,
  `field_siteDescription` varchar(140) COLLATE utf8_unicode_ci DEFAULT NULL,
  `field_siteKeywords` text COLLATE utf8_unicode_ci,
  `field_sectionTitle` text COLLATE utf8_unicode_ci,
  `field_sectionSubtitle` text COLLATE utf8_unicode_ci,
  `field_asideTitle` text COLLATE utf8_unicode_ci,
  `field_asideBlurb` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `dateCreated` datetime NOT NULL,
  `dateUpdated` datetime NOT NULL,
  `uid` char(36) COLLATE utf8_unicode_ci NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `tmp_content_elementId_locale_unq_idx` (`elementId`,`locale`),
  KEY `tmp_content_title_idx` (`title`),
  KEY `tmp_content_locale_fk` (`locale`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

