INSERT INTO `tmp_matrixcontent_postbuilder` (`id`, `elementId`, `locale`, `field_textBlock_text`, `field_textBlock_position`, `field_textBlock_paddingTop`, `field_textBlock_paddingBottom`, `field_photos_paddingTop`, `field_photos_paddingBottom`, `field_photos_photoWidth`, `field_recipe_ingredientsList`, `field_callout_text`, `field_callout_cite`, `field_callout_textColor`, `field_callout_paddingTop`, `field_callout_paddingBottom`, `field_recipe_method`, `field_recipe_servings`, `field_photos_mobileStack`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('12', '29', 'en_us', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '[{\"col2\":\"5 cups\",\"col1\":\"apples, peeled, cored and sliced\"},{\"col2\":\"1 1\\/3 cup\",\"col1\":\"brown sugar\"},{\"col2\":\"1 1\\/2\",\"col1\":\"  tsp cinnamon\"},{\"col2\":\"3 tbsp \",\"col1\":\"butter\"},{\"col2\":\"1\\/3 cup \",\"col1\":\"melted butter\"},{\"col2\":\"1 cup \",\"col1\":\"flour\"}]', NULL, NULL, NULL, NULL, NULL, '<ol><li><em>Preheat oven to 375°.</em></li>\n	<li><em>In a medium sized mixing bowl coat apples with ⅓ cup brown sugar and ½ tsp cinnamon. Thinly slice 3 tbsp of butter and layer in the bottom of a 12 inch cast iron skillet. Pour the apples over the butter.</em></li>\n	<li><em>In another mixing bowl combine remaining sugar, cinnamon, flour and melted butter until a fully incorporated. Using your fingers crumble over the top of the apples.</em></li>\n	<li><em>Bake on the center rack for 1 hour or until bubbling. I think it goes without saying this should be served with vanilla ice cream.</em></li>\n</ol>', '5', NULL, '2015-09-25 06:14:43', '2015-10-09 01:06:05', '4b9c67b0-8cea-4f3f-bad3-74d4cb21b2e9');
